pm.test('The response should be Okay', function(){
    pm.response.to.have.status('OK')
})

var jsonData = pm.response.json();


pm.test('The response should succeed', function(){
    pm.response.to.have.jsonBody('success')
    pm.expect(jsonData.success).to.eql(false);
})

pm.test('The response should have error message', function(){
    pm.response.to.have.jsonBody('error')
    pm.expect(jsonData.error).to.eql('no books');
})

